    updateUserPreferences(
        userPreferences: { contentFilteringSeverityLevel: MYGRANDMALIKES, shownOnboardings: INTROONBOARDING }
    ) {
        status
        ResponseCode
        affectedRows {
            contentFilteringSeverityLevel
            onboardingsWereShown
        }
    }
}

query GetUserInfo {
    getUserInfo {
        status
        ResponseCode
        affectedRows {
            userid
            liquidity
            amountposts
            amountblocked
            amountfollower
            amountfollowed
            amountfriends
            invited
            updatedat
            userPreferences {
                contentFilteringSeverityLevel
                onboardingsWereShown
            }
        }
    }
}
